import React, { useState, useEffect, useRef } from 'react';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';

function App() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const fileInputRef = useRef(null);
  const [locations, setLocations] = useState([]);
  const [newLocation, setNewLocation] = useState({ name: '', category: '', latitude: '', longitude: '' });
  const [uploadStatus, setUploadStatus] = useState('');
  const [uploadErrors, setUploadErrors] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    if (map.current) return;

    map.current = new maplibregl.Map({
      container: mapContainer.current,
      style: 'https://demotiles.maplibre.org/style.json',
      center: [0, 0],
      zoom: 2
    });

    map.current.on('load', () => {
      fetchLocations();
    });

    return () => {
      if (map.current) {
        map.current.remove();
        map.current = null;
      }
    };
  }, []);

  const fetchLocations = async () => {
    try {
      const response = await fetch('http://localhost:8000/locations/geojson');
      const data = await response.json();
      setLocations(data.features);

      if (map.current) {
        if (map.current.getSource('locations')) {
          map.current.getSource('locations').setData(data);
        } else {
          map.current.addSource('locations', {
            type: 'geojson',
            data: data
          });

          map.current.addLayer({
            id: 'locations',
            type: 'circle',
            source: 'locations',
            paint: {
              'circle-radius': 6,
              'circle-color': '#B42222'
            }
          });
        }
      }
    } catch (error) {
      console.error('Error fetching locations:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/locations', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newLocation)
      });
      const data = await response.json();
      setNewLocation({ name: '', category: '', latitude: '', longitude: '' });
      fetchLocations();
    } catch (error) {
      console.error('Error adding location:', error);
    }
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.name.endsWith('.csv')) {
        setUploadStatus('Please select a CSV file');
        setUploadErrors([]);
        setSelectedFile(null);
        return;
      }
      setSelectedFile(file);
      setUploadStatus('File selected: ' + file.name);
      setUploadErrors([]);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setUploadStatus('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      setUploadStatus('Uploading...');
      setUploadErrors([]);
      const response = await fetch('http://localhost:8000/import', {
        method: 'POST',
        body: formData,
      });
      const result = await response.json();

      if (result.error) {
        setUploadStatus('Error: ' + result.error);
        setUploadErrors([]);
      } else {
        setUploadStatus(`Successfully imported ${result.imported} locations`);
        if (result.errors && result.errors.length > 0) {
          setUploadErrors(result.errors);
        }
        if (fileInputRef.current) {
          fileInputRef.current.value = '';
        }
        setSelectedFile(null);
      }
      fetchLocations();
    } catch (error) {
      console.error('Error uploading file:', error);
      setUploadStatus('Error uploading file');
      setUploadErrors([]);
    }
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold text-gray-900 mb-6">Location Tracker</h1>

      <div ref={mapContainer} className="map-container mb-6" />

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-sm">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Add New Location</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="text"
                placeholder="Name"
                value={newLocation.name}
                onChange={(e) => setNewLocation({ ...newLocation, name: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="text"
                placeholder="Category"
                value={newLocation.category}
                onChange={(e) => setNewLocation({ ...newLocation, category: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="number"
                placeholder="Latitude"
                value={newLocation.latitude}
                onChange={(e) => setNewLocation({ ...newLocation, latitude: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="number"
                placeholder="Longitude"
                value={newLocation.longitude}
                onChange={(e) => setNewLocation({ ...newLocation, longitude: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button 
                type="submit"
                className="w-full bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              >
                Add Location
              </button>
            </form>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-sm">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Import Locations</h2>
            <div className="space-y-4">
              <div className="flex gap-4">
                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".csv"
                  onChange={handleFileSelect}
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <button
                  onClick={handleUpload}
                  disabled={!selectedFile}
                  className={`px-6 py-2 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-offset-2 ${
                    selectedFile 
                      ? 'bg-green-600 hover:bg-green-700 focus:ring-green-500' 
                      : 'bg-gray-400 cursor-not-allowed'
                  }`}
                >
                  Upload
                </button>
              </div>

              {uploadStatus && (
                <div className={`p-4 rounded-md ${
                  uploadStatus.includes('Error') 
                    ? 'bg-red-50 text-red-700' 
                    : 'bg-green-50 text-green-700'
                }`}>
                  {uploadStatus}
                </div>
              )}

              {uploadErrors.length > 0 && (
                <div className="bg-orange-50 p-4 rounded-md text-orange-700">
                  <h4 className="font-semibold mb-2">Import Warnings:</h4>
                  <ul className="list-disc list-inside space-y-1">
                    {uploadErrors.map((error, index) => (
                      <li key={index}>{error}</li>
                    ))}
                  </ul>
                </div>
              )}

              <div className="text-sm text-gray-500">
                CSV format: name,category,latitude,longitude
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Locations List</h2>
          <ul className="space-y-3">
            {locations.map(loc => (
              <li 
                key={loc.properties.id}
                className="p-4 bg-gray-50 rounded-md hover:bg-gray-100 transition-colors"
              >
                <div className="font-medium text-gray-900">{loc.properties.name}</div>
                <div className="text-sm text-gray-600">{loc.properties.category}</div>
                <div className="text-sm text-gray-500">
                  Coordinates: {loc.geometry.coordinates[1]}, {loc.geometry.coordinates[0]}
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
